using System;
using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.Networking;
using TMPro;


public class APILoader : MonoBehaviour
{
    // JSON データ化するクラス
    [Serializable]
    public class CO2SensorData
    {
        public float CO2;
        public float Temperature;
        public float Humidity;
        public string type;
    }

    void Start()
    {
        // 一度実行ならこれ
        // StartCoroutine(GetAPICore());

        // 今回は定期実行
        InvokeRepeating("GetAPI", 1.0f, 1.0f);
    }

    // StartCoroutine でアクセスできるようにするため
    void GetAPI()
    {
        StartCoroutine(GetAPICore());
    }

    IEnumerator GetAPICore()
    {
        // API URL
        UnityWebRequest request = UnityWebRequest.Get("https://vantan-tc-tseigo-2022-22bqvc.herokuapp.com/sensor/data");

        yield return request.SendWebRequest();

        if (request.isNetworkError || request.isHttpError)
        {
            Debug.Log(request.error);
        }
        else
        {
            // 結果をテキストとして表示します
            Debug.Log(request.downloadHandler.text);

            // まずテキストデータとして取得
            string json = (string)request.downloadHandler.text;

            Debug.LogFormat("json: {0}", json);

            // そのうえで JSON データ化
            CO2SensorData data = JsonUtility.FromJson(json);

            // TextAPI に反映
            GameObject.Find("TextAPI").GetComponent().text = string.Format("M5Stack+Grove\nCO2 : {0}", (int)data.CO2);

            // 2000 ppm 越えたら文字を赤くする
            if (data.CO2 > 2000)
            {
                GameObject.Find("TextAPI").GetComponent().color = Color.red;
            } else
            {
                GameObject.Find("TextAPI").GetComponent().color = Color.white;
            }
        }


    }
}

